/**
 * promise的api方法：
 * promise construct
 * then
 * resolve/reject
 * catch
 * all
 * race
 * 
 * promise状态：
 * pending: 初始状态
 * fulfilled：操作成功完成
 * rejected：操作失败
 */


 class Promise {
     constructor(func){
        this.state = 'pending'; // fulfilled rejected
        this.value = '';

        let resolve = (data) => {
            this.state = 'fulfilled';
            this.value = data;
        }

        let reject = (data) => {
            this.state = 'rejected';
            this.value = data;
        }

        func(resolve, reject);
     }
 }
 

